/* eslint jsx-a11y/anchor-is-valid: 0 */

import React, { useEffect, useState } from "react";
import {
  Container,
  Row,
  Col,
  Card,
  CardBody,
  CardFooter,
  Badge,
  Button,
} from "shards-react";
const mapboxgl = require("mapbox-gl/dist/mapbox-gl.js");
import { addDataLayer } from "../map/addDataLayer";
import { initializeMap } from "../map/initializeMap";


const BlogPosts = () => {
  const [pageIsMounted, setPageIsMounted] = useState(false);
  const [Map, setMap] = useState();
  const { data, error } = useSWR("/api/liveMusic", fetcher);

  if (error) {
    console.error(error);
  }

  mapboxgl.accessToken =
    "pk.eyJ1Ijoid2FubmFkYyIsImEiOiJjazBja2M1ZzYwM2lnM2dvM3o1bmF1dmV6In0.50nuNnApjrJYkMfR2AUpXA";

  useEffect(() => {
    setPageIsMounted(true);

    let map = new mapboxgl.Map({
      container: "my-map",
      style: "mapbox://styles/mapbox/streets-v11",
      center: [21.2297, 52.0122],
      zoom: 4.5,
      pitch: 45,
      //   maxBounds: [
      //     // [-77.875588, 38.50705], // Southwest coordinates
      //     // [-76.15381, 39.548764], // Northeast coordinates
      //   ],
    });

    initializeMap(mapboxgl, map);
    setMap(map);
  }, []);

  useEffect(() => {
    if (pageIsMounted && data) {
      Map.on("load", function () {
        addDataLayer(Map, data);
      });
    }
  }, [pageIsMounted, setMap, data, Map]);

  return (
    <Container>
      <img
        width="100%"
        src="https://storage.googleapis.com/nextgrid_github_repo_visuals/Github%20Graphics%20/poland.jpg"
        alt=""
      />
      <div id="my-map" className="map" />
    </Container>
  );
};

export default BlogPosts;
